pragma solidity ^0.4.24;

import "./library/SafeMath.sol";

contract Lottery {

    //////////////////////回调事件//////////////////////

    event buyCards(address adr, uint value, uint start, uint end);
    event win(address adr, uint id, uint level, uint bonus);
    event sendBonusLog(address adr, uint amount);

    //////////////////////私有成员//////////////////////

    uint private keysLeft = 100000;
    uint private _keyPrice = 0.01 ether;
    uint private randNonce = 0;

    struct Rule{
        uint level;
        uint bonus;
    }
    Rule[] private rules;

    struct ScratchCard{
        address adr;
        uint number;
        uint drew;
    }
    mapping(uint => ScratchCard) private cardList;
    mapping(address => uint) private balanceList;

    address private owner;

    constructor() public{
        owner = msg.sender;
        rules.push(Rule(1,190 ether));
        rules.push(Rule(10,10 ether));
        rules.push(Rule(100,1 ether));
        rules.push(Rule(1000,0.1 ether));
        rules.push(Rule(2000,0.05 ether));
        rules.push(Rule(10000,0.02 ether));
        rules.push(Rule(20000,0.01 ether));
    }

    //////////////////////公有方法//////////////////////

    function getKeysLeft() public returns(uint){
        return keysLeft;
    }

    function getCards(uint id) public returns (bool,uint,uint){
        if(msg.sender==cardList[id].adr){
            return (true,getLevel(cardList[id].number),cardList[id].drew);
        }
        else{
            return (false,0,1);
        }
    }

    function getLevel(uint randomNum) public returns (uint){
        uint magicNum = 0;
        for (uint i = 0; i < rules.length; i++) {
            if(randomNum > magicNum && randomNum <= magicNum + rules[i].level) {
                return i+1;
            }
        }
        return 0;
    }

    function getBalances() public returns(uint){
        return balanceList[msg.sender];
    }

    function topUp() payable public returns(uint,uint){
        uint acquiredKeys = msg.value / _keyPrice;
        require(acquiredKeys <= keysLeft);
        for (uint i = 0; i < acquiredKeys; i++){
            uint randomNum = getRandomNum(1, keysLeft);
            keysLeft = keysLeft - 1;
            cardList[keysLeft] = ScratchCard(msg.sender,randomNum,0);
        }
        emit buyCards(msg.sender, msg.value, keysLeft+acquiredKeys-1,keysLeft);
        return (keysLeft+acquiredKeys-1,keysLeft);
    }

    function scratchCard(uint id) public returns(uint,uint,uint){
        require((0==cardList[id].drew) && (msg.sender == cardList[id].adr));
        cardList[id].drew = 1;
        uint level = getLevel(cardList[id].number);
        if(level!=0){
            rules[level-1].level = rules[level-1].level -1; // 刮出大奖需要抢，可能被别人抢走
            emit win(msg.sender, id, level, rules[level-1].bonus);
            balanceList[msg.sender] = balanceList[msg.sender] + rules[level-1].bonus;
            return (id, level, rules[level-1].bonus);
        }else{
            emit win(msg.sender, id, 0, 0);
            return (id, 0,0);
        }
    }

    // TODO: what if account's eth is not enough
    function sendBonus(uint amount) payable public  {
        require(amount <= balanceList[msg.sender] && amount>0);
        balanceList[msg.sender] = balanceList[msg.sender] - amount;
        emit sendBonusLog(msg.sender, amount);
        msg.sender.transfer(amount);
    }

    // to withdraw eth to owner's account
    function withdraw(uint amount) payable public {
        if(msg.sender != owner) {
            return;
        }
        msg.sender.transfer(amount);
    }


    //////////////////////私有方法//////////////////////
    function getRandomNum(uint start, uint end) private returns (uint) {
        require(end >= start);
        uint random = uint(keccak256(now, msg.sender, randNonce)) % (end - start + 1) + start;
        return random;
    }

}
